
public class ExemploArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] meuArray = new int[5];
		
		meuArray[0] = 10;
		meuArray[1] = 20;
		meuArray[2] = 30;
		
		System.out.println(meuArray[1]);
		System.out.println(meuArray[0]);
		System.out.println(meuArray[2]);
		System.out.println(meuArray[3]);
		System.out.println(meuArray[4]);
		
		String[] diasDaSemana = new String[7];
		
		diasDaSemana[0] = "Domingo";
		diasDaSemana[1] = "Segunda-feira";
		diasDaSemana[2] = "Terça-feira";
		diasDaSemana[3] = "Quarta-feira";
		diasDaSemana[4] = "Quinta-feira";
		diasDaSemana[5] = "Sexta-feira";
		diasDaSemana[6] = "Sábado";
		
		System.out.println(diasDaSemana[4]);
		System.out.println(diasDaSemana);
		
		String[] teste = {"Um", "Dois", "Três"};
		System.out.println(teste.length);
		
		teste[2] = "outro";
		System.out.println(teste[2]);
		
		
		
		
		
	}

}
